// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Import ERC-20 contract from OpenZeppelin
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

// EnergyToken contract - represents tokenized energy (ERC-20)
contract EnergyToken is ERC20 {
    address public owner;

    // Constructor for the EnergyToken
    constructor(uint256 initialSupply) ERC20("EnergyToken", "ETK") {
        _mint(msg.sender, initialSupply); // Mint initial supply to the contract owner
        owner = msg.sender; // Set the contract deployer as the owner
    }

    // Function to mint tokens, restricted to the owner
    function mint(address to, uint256 amount) public {
        require(msg.sender == owner, "Only owner can mint tokens");
        _mint(to, amount);
    }
}

// EnergyMarket contract - decentralized marketplace for energy trading
contract EnergyMarket {
    EnergyToken public energyToken; // EnergyToken instance
    uint256 public tokenPrice = 1 ether; // Price of 1 EnergyToken in ETH (modifiable)

    // Struct for an energy offer (offered by a producer)
    struct EnergyOffer {
        uint256 energyAmount; // Amount of energy (in kWh)
        uint256 pricePerUnit; // Price per unit of energy (per kWh)
        address producer;     // Address of the producer
    }

    // Array to store energy offers
    EnergyOffer[] public energyOffers;

    // Mapping of addresses to energy consumption balances (tracking purchased energy)
    mapping(address => uint256) public consumerEnergyBalance;

    // Mapping of user addresses to hashed SSNs for identity verification
    mapping(address => bytes32) public userHashes;

    // Mapping to track roles (consumer/producer)
    mapping(address => string) public roles;

    // Events for transparency
    event EnergyOffered(uint256 offerId, uint256 energyAmount, uint256 pricePerUnit, address producer);
    event EnergyPurchased(uint256 offerId, uint256 energyAmount, uint256 pricePerUnit, address consumer);
    event TokensPurchased(address buyer, uint256 amount, uint256 ethPaid);
    event TokensSold(address seller, uint256 amount, uint256 ethReceived);
    event UserRegistered(address user, string role, bytes32 ssnHash);

    // Constructor to initialize EnergyToken
    constructor(uint256 initialSupply) {
        energyToken = new EnergyToken(initialSupply); // Deploy the EnergyToken with initial supply
    }

    // Registration function to link users to their SSN hash
    function registerUser(string memory ssn, string memory role) public {
        require(userHashes[msg.sender] == 0, "User already registered");
        require(
            keccak256(abi.encodePacked(role)) == keccak256(abi.encodePacked("consumer")) ||
            keccak256(abi.encodePacked(role)) == keccak256(abi.encodePacked("producer")),
            "Invalid role"
        );

        // Store the hashed SSN and assign the role
        userHashes[msg.sender] = keccak256(abi.encodePacked(ssn));
        roles[msg.sender] = role;

        emit UserRegistered(msg.sender, role, userHashes[msg.sender]);
    }

    // Function for consumers to buy EnergyTokens using ETH
    function buyTokens() public payable {
        require(userHashes[msg.sender] != 0, "User not registered");
        require(
            keccak256(abi.encodePacked(roles[msg.sender])) == keccak256(abi.encodePacked("consumer")),
            "Only consumers can buy tokens"
        );

        require(msg.value > 0, "You must send ETH to buy tokens");

        uint256 tokensToMint = msg.value / tokenPrice; // Calculate the number of tokens to mint
        require(tokensToMint > 0, "Insufficient ETH sent for tokens");

        energyToken.mint(msg.sender, tokensToMint); // Mint tokens to the buyer

        emit TokensPurchased(msg.sender, tokensToMint, msg.value); // Emit event
    }

    // Function for consumers to sell EnergyTokens back to the contract
    function sellTokens(uint256 tokenAmount) public {
        require(userHashes[msg.sender] != 0, "User not registered");
        require(
            keccak256(abi.encodePacked(roles[msg.sender])) == keccak256(abi.encodePacked("consumer")),
            "Only consumers can sell tokens"
        );

        require(tokenAmount > 0, "You must specify an amount to sell");
        require(energyToken.balanceOf(msg.sender) >= tokenAmount, "You don't have enough tokens to sell");

        uint256 ethAmount = tokenAmount * tokenPrice; // Calculate the ETH equivalent
        require(address(this).balance >= ethAmount, "Contract doesn't have enough ETH to buy tokens");

        energyToken.transferFrom(msg.sender, address(this), tokenAmount); // Transfer tokens back to the contract
        payable(msg.sender).transfer(ethAmount); // Send ETH to the seller

        emit TokensSold(msg.sender, tokenAmount, ethAmount); // Emit event
    }

    // Function for producers to list their energy for sale
    function listEnergyForSale(uint256 energyAmount, uint256 pricePerUnit) public {
        require(userHashes[msg.sender] != 0, "User not registered");
        require(
            keccak256(abi.encodePacked(roles[msg.sender])) == keccak256(abi.encodePacked("producer")),
            "Only producers can list energy"
        );

        require(energyToken.balanceOf(msg.sender) >= energyAmount, "Not enough EnergyTokens to list");

        energyToken.transferFrom(msg.sender, address(this), energyAmount);

        energyOffers.push(EnergyOffer({
            energyAmount: energyAmount,
            pricePerUnit: pricePerUnit,
            producer: msg.sender
        }));

        emit EnergyOffered(energyOffers.length - 1, energyAmount, pricePerUnit, msg.sender);
    }

    function buyEnergy(uint256 offerId, uint256 energyAmount) public {
        require(userHashes[msg.sender] != 0, "User not registered");
        require(
            keccak256(abi.encodePacked(roles[msg.sender])) == keccak256(abi.encodePacked("consumer")),
            "Only consumers can buy energy"
        );

        require(offerId < energyOffers.length, "Invalid offer ID");
        EnergyOffer storage offer = energyOffers[offerId];

        // Ensure the consumer is buying the full offer amount
        require(energyAmount == offer.energyAmount, "You can only buy the full amount");

        uint256 totalPrice = energyAmount * offer.pricePerUnit;
        require(energyToken.balanceOf(msg.sender) >= totalPrice, "Not enough EnergyTokens to buy");

        // Transfer tokens and adjust balances
        energyToken.transferFrom(msg.sender, offer.producer, totalPrice);
        consumerEnergyBalance[msg.sender] += energyAmount;

        // Remove the offer from the list as it's fully purchased
        energyOffers[offerId] = energyOffers[energyOffers.length - 1]; // Swap with the last offer
        energyOffers.pop(); // Remove the last offer

        emit EnergyPurchased(offerId, energyAmount, offer.pricePerUnit, msg.sender);
    }

}
